import React from 'react'
import styled from 'styled-components'
import {ArrowLeftOutlined, ArrowRightOutlined} from '@material-ui/icons'

const Slider = () => {
    return (
       <Container>
           <Arrow direction="left">
               <ArrowLeftOutlined/>
           </Arrow>
           <Wrapper>
               <Slide>
                    <ImgContainer>
                            <Image src="https://i.ibb.co/DG69bQ4/2.png"/>
                    </ImgContainer>
                    <InfoContainer>
                        <Title>Summer Sale</Title>
                        <Desc>DON'T COMPROMISE ON PRICE! GET 30% OFF ON NEW ARRIVALS.</Desc>
                        <Button>SHOW NOW</Button>
                    </InfoContainer>
                </Slide>
           </Wrapper>
           <Arrow direction="right">
               <ArrowRightOutlined/>
           </Arrow>
       </Container>
    )
}

const Container = styled.div`
    width: 100%;
    height: 100vh;
    display: flex;
    position: relative;
    overflow: hidden;
    overflow: hidden;
`;
const Arrow = styled.div`
    width: 50px;
    height: 50px;
    background-color: white;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 0;
    bottom: 0;
    margin: auto;
    left: ${props => props.direction === "left" && "10px"};
    right: ${props => props.direction === "right" && "10px"};
    cursor: pointer;
    opacity: 0.5;
`;

const Wrapper = styled.div`
    height: 100%;
    display: flex;
`;

const Slide = styled.div`
    display: flex;
    align_items:center;
    width: 100vw;
    height: 100vh;
`;

const ImgContainer = styled.div`
    height: 100%;
    flex:1;
`;

const Image = styled.img`
    height: 90%;
`;
const InfoContainer = styled.div`
    flex:1;
    padding: 50px;
`;

const Title = styled.h1`
  font-size: 70px;
`;

const Desc = styled.p`
  margin: 50px 0px;
  font-size: 20px;
  font-weight: 500;
  letter-spacing: 3px;
`;

const Button = styled.button`
  padding: 10px;
  font-size: 20px;
  background-color: transparent;
  cursor: pointer;
`;

export default Slider
